import React, { useEffect,useState} from 'react'
import Navbar from './Navbar'
import { Link,useNavigate} from 'react-router-dom';
import '../CSS/Home.css';

export default function Home() {
  console.log(localStorage.getItem("user"));
  const navigate=useNavigate();
  const [data, setdata] = useState([])
useEffect(() => {
  const token=localStorage.getItem('jwt');
  if (!token) {
    navigate('/SignIn')
  }
  fetch("http://localhost:5000/allposts",{
    headers: {
      "authorization": "Bearer " + localStorage.getItem('jwt')
    },
  }).then(res=>res.json())
  .then(result=> setdata(result))
  .catch(err=>console.log(err))
 
}, [])
const likePost = (id) => {
  fetch("http://localhost:5000/like", {
    method: "put",
    headers: {
      "Content-Type": "application/json",
      Authorization: "Bearer " + localStorage.getItem("jwt"),
    },
    body: JSON.stringify({
      postId: id,
    }),
  })
    .then((res) => res.json())
    .then((result) => {
      return Post.findById(result._id); // Assuming 'Post' is your Mongoose model
    })
    .then((updatedPost) => {
      const newData = data.map((posts) => {
        if (posts._id == updatedPost._id) {
          return updatedPost;
        } else {
          return posts;
        }
      });
      setdata(newData);
      console.log(updatedPost);
    })
    .catch((error) => {
      console.error(error);
    });
};

const unlikePost = (id) => {
  fetch("http://localhost:5000/unlike", {
    method: "put",
    headers: {
      "Content-Type": "application/json",
      Authorization: "Bearer " + localStorage.getItem("jwt"),
    },
    body: JSON.stringify({
      postId: id,
    }),
  })
    .then((res) => res.json())
    .then((result) => {
      const newData = data.map((posts) => {
        if (posts._id == result._id) {
          return result;
        } else {
          return posts;
        }
      });
      setdata(newData);
      console.log(result);
    });
};

  

  
  return (
    <>
    <Navbar/>
    <div className="Home">
    {data.map((posts)=>{
      
      console.log(posts)
      return(
        <div className='home-card'>
        <div className="home-card-header">
          <div className='home-card-pic'>
            <img src="https://w7.pngwing.com/pngs/178/595/png-transparent-user-profile-computer-icons-login-user-avatars-thumbnail.png"
              alt=""/>
            <h5 className='userName'>{posts.postedBy.UserName}</h5>
          
          </div>
        </div>
        <div className="home-card-img">
          <img src={posts.photo}
            alt="" />
        </div>
        <div className="home-card-content">
          {       
          
            posts.likes.includes(JSON.parse(localStorage.getItem("user"))._id)
            ?
            (<span
              className="material-symbols-outlined material-symbols-outlined-red"
              onClick={() => {
                unlikePost(posts._id);
              }}
            >
              favorite
            </span>
          ) : (
            <span
              className="material-symbols-outlined"
              onClick={() => {
                likePost(posts._id);
              }}
            >
              favorite
            </span>
          )
          }
        
       
        <p>1 like</p>
        {/* <br/> */}
        <p>{posts.body}</p>
        </div>
        <div className="add-comment">
        <i class='bx bx-smile'></i>
        <input type="text"placeholder='add a comment' name="" id="" />
        <button className='comment'>Post</button>
        </div>


      </div>
      )
    })}
      
      </div>

    </>

  )
}
