import React, { useEffect,useState} from 'react'
import Navbar from './Navbar'
import { Link } from 'react-router-dom';
import '../CSS/Home.css';

export default function Home() {
  const [token, setToken] = useState("");

  const storedDataString = localStorage.getItem('token');
    const storedData = JSON.parse(storedDataString);
   token = storedData.token;
    const [data, setdata] = useState([])
  const loginStatus = () => {
    
    // console.log(storedDataString)
    // console.log(storedData)
    console.log(token)
    if (token) {
      return [
        <>
          <Navbar />

        </>
      ]
    } else {
      return [
        <div className='login_dailog'>
          <h2 id='head'>Login to Continue</h2>
          <Link to='/SignIn' className='continue'>Click here to continue</Link>
        </div>
      ]
    }
  }
useEffect(() => {
  // Fetching post
  fetch("http://localhost:5000/allposts",{
    headers: {
      "authorization": "Bearer " + token
    },
  }).then(res=>res.json())
  .then(result=> setdata(result))
  .catch(err=>console.log(err))
}, [])

  return (
    <>
      {loginStatus()}
     
    <div className="Home">
    {data.map((posts)=>{
      console.log(posts)
      return(
        <div className='home-card'>
        <div className="home-card-header">
          <div className='home-card-pic'>
            <img src="https://w7.pngwing.com/pngs/178/595/png-transparent-user-profile-computer-icons-login-user-avatars-thumbnail.png"
              alt=""/>
            <h5 className='userName'>{posts.postedBy.UserName}</h5>
          
          </div>
        </div>
        <div className="home-card-img">
          <img src={posts.photo}
            alt="" />
        </div>
        <div className="home-card-content">
        <i className='bx bx-heart' id='heart-icon'></i>
        <p>1 like</p>
        {/* <br/> */}
        <p>{posts.body}</p>
        </div>
        <div className="add-comment">
        <i class='bx bx-smile'></i>
        <input type="text"placeholder='add a comment' name="" id="" />
        <button className='comment'>Post</button>
        </div>


      </div>
      )
    })}
      
      </div>

    </>

  )
}
