import React from 'react'

function Navbar() {
  // navbar Sidebar 
                    //Star
                    const body = document.querySelector("body");
                    const darkLight = document.querySelector("#darkLight");
                    const sidebar = document.querySelector(".sidebar");
                    const submenuItems = document.querySelectorAll(".submenu_item");
                    const sidebarOpen = document.querySelector("#sidebarOpen");
                    const sidebarClose = document.querySelector(".collapse_sidebar");
                    const sidebarExpand = document.querySelector(".expand_sidebar");
                    sidebarClose.addEventListener("click", () => {
                        sidebar.classList.toggle("close", true);
                        sidebar.classList.toggle("hoverable", true);
                    });

                    sidebarExpand.addEventListener("click", () => {
                        sidebar.classList.toggle("close", false);
                        sidebar.classList.toggle("hoverable", false);
                    });

                    // Simplify sidebar mouse enter/leave event
                    sidebar.addEventListener("mouseenter", () => {
                        sidebar.classList.toggle("close", false);
                    });

                    sidebar.addEventListener("mouseleave", () => {
                        sidebar.classList.toggle("close", true);
                    });

                    // Use classList.replace for dark/light mode
                    darkLight.addEventListener("click", () => {
                        body.classList.toggle("dark");
                        darkLight.classList.replace("bx-sun", "bx-moon");
                    });

                    // Use classList.toggle for submenu items
                    submenuItems.forEach((item, index) => {
                        item.addEventListener("click", () => {
                            item.classList.toggle("show_submenu");
                            submenuItems.forEach((item2, index2) => {
                                if (index !== index2) {
                                    item2.classList.remove("show_submenu");
                                }
                            });
                        });
                    });

                    // Use classList.toggle for sidebar initial state based on screen width
                    // Add an event listener for the menu icon to toggle the sidebar
                    sidebarOpen.addEventListener("click", () => {
                        sidebar.classList.toggle("close");
                    });

                    // Use classList.toggle for sidebar initial state based on screen width
                    if (window.innerWidth < 768) {
                        sidebar.classList.add("close");
                    } else {
                        sidebar.classList.remove("close");
                    }
                    // END

                    // Popup
                    document.getElementById('uploadBtn').addEventListener('click', function () {
                        document.getElementById('overlay').style.display = 'block';
                        document.getElementById('popup').style.display = 'block';
                    });

                    document.getElementById('closeBtn').addEventListener('click', function () {
                        document.getElementById('overlay').style.display = 'none';
                        document.getElementById('popup').style.display = 'none';
                    });

                    // Inserting POST
                    document.querySelector('form').addEventListener('submit', function (e) {
                        e.preventDefault();
                        postImage();
                    });

                    function postImage() {
                        var imageInput = document.getElementById('file');
                        var captionInput = document.getElementById('captionInput');
                        var userIdInput = document.getElementById('userIdInput');

                        if (imageInput.files.length === 0) {
                            alert('Please select an image');
                            return;
                        }

                        var postElement = document.createElement('div');
                        postElement.classList.add('post-container');

                        // Display the user's ID or name
                        var userIdElement = document.createElement('p');
                        userIdElement.textContent = 'User ID: ' + userIdInput.value; // Change this line accordingly
                        postElement.appendChild(userIdElement);

                        // Display the image
                        var imageElement = document.createElement('img');
                        imageElement.src = URL.createObjectURL(imageInput.files[0]);
                        imageElement.classList.add('post-image');
                        imageElement.alt = 'Posted Image';
                        postElement.appendChild(imageElement);

                        // Display the caption below the image if it's not empty
                        if (captionInput.value.trim() !== '') {
                            var captionElement = document.createElement('p');
                            captionElement.classList.add('caption');
                            captionElement.textContent = captionInput.value;
                            postElement.appendChild(captionElement);
                        }

                        var postContainer = document.getElementById('postContainer');
                        postContainer.insertBefore(postElement, postContainer.firstChild);

                        imageInput.value = '';
                        captionInput.value = '';
                        userIdInput.value = '';

                        document.getElementById('overlay').style.display = 'none';
                        document.getElementById('popup').style.display = 'none';
                    }
  return (
   <>
     <div className="main-container">
        {/* <!-- navbar --> */}
  <nav></nav>
          {/* <div className="logo_item"> */}
          <div>
          <i className="bx bx-menu" id="sidebarOpen"></i>
            <img src="logo.jpeg" alt="Pillai College"/>Pillai College
          </div>
           
            </div>

          <div className="search_bar">
            <input type="text" placeholder="Search" />
        </div>

            {/* <!-- Upload Button--> */}
            {/* <!-- Start --> */}
        <button id="uploadBtn">Upload</button>

          <div id="overlay"></div>
            <div id="popup">
              <span id="closeBtn">&times;</span>
            
              <form action="#" method="post">
                <input type="hidden" id="userIdInput" name="userIdInput" value="none"/>
                  <label htmlFor="file">Select a file:</label>
                <input type="file" id="file" name="file" accept=".jpg, .jpeg, .png" capture="environment"/>
                      <br/><br/>
                  <label htmlFor="captionInput">Caption:</label>
                <input type="text" id="captionInput" name="captionInput"/>
                      <br/><br/>
                <input type="submit" value="Submit"/>

              </form>
            </div>
            {/* <!-- End --> */}

          <div className="navbar_content">
              <i className="bi bi-grid"></i>
              <i className='bx bx-sun' id="darkLight"></i>
              <i className='bx bx-bell'></i>
              <img src="profile.webp" alt="" className="profile" />
          </div>
 

        {/* <!-- sidebar --> */}
        <div className="main-content">
            <nav className="sidebar">
                <div className="menu_content">
                    <ul className="menu_items">
                        <div className="menu_title menu_dahsboard"></div>
                        {/* <!-- duplicate or remove this li tag if you want to add or remove navlink with submenu -->
                        <!-- start --> */}
                        <a href="main.html">

                            <li className="item">
                                <div className="nav_link submenu_item">
                                    <span className="navlink_icon">
