import React,{useState}from 'react'
import Navbar from './Navbar'
import '../CSS/Notice.css'
// import ImgIcon from '../img/Img-icon.png';
export default function Notice() {
  const [image, setImage] = useState("")
  const loadfile = (event) => {
    var output = document.getElementById("output");
    output.src = URL.createObjectURL(event.target.files[0]);
    output.onload = function () {
      URL.revokeObjectURL(output.src); // free memory
    };
  };
    return (
    <>
    <Navbar/>
      {/* <!-- college notice board --> */}
            {/* <!-- Start --> */}
            {/* <!-- Modify the notice container to allow users to post either text or an image --> */}

            <div className="notices-container">
                <h2>College Notice Board</h2>
                <div id="noticeList"></div>
            </div>
            <hr/>
            <div className="notice-form-container">
                <form id="addNoticeForm" enctype="multipart/form-data">
                    <div className="form-group">
                        <label htmlFor="noticeText">Add Notice:</label>
                        <textarea className="form-control" id="noticeText" rows="3" required></textarea>
                    </div>
                    <div className="form-group">
                        <label htmlFor="uploadImage">Upload Image (Optional):</label>
                        <img src='https://static.thenounproject.com/png/777906-200.png' id='output'/>
                        <input type="file"  onChange={(event) => {loadfile(event);
                                            setImage(event.target.files[0])}}
                         name="uploadImage" id="uploadImage" accept="image/*" />
                    </div>
                    <button type="submit" className="btn btn-primary">Post Notice</button>
                </form>

                {/* <!-- Add a container for displaying the uploaded image --> */}
                {/* <!-- <div id="imageViewer" className="image-viewer"></div> --> */}
            </div>
       
        {/* <!-- End --> */}

    </>
  )
}
