import React, { useState,useEffect } from 'react';
import Navbar from './Navbar';
import '../CSS/Notice.css';
import { toast,ToastContainer } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
import AllNotice from './screens/AllNotice';
export default function Notice() {
  const [imageSrc, setImageSrc] = useState('');
  const storedDataString = localStorage.getItem('token');
  const storedData = JSON.parse(storedDataString);
  const [body, setBody] = useState("");
  const [image, setImage] = useState("")
  const [url, setUrl] = useState("")
  const navigate = useNavigate()

  // Toast functions
  const notifyA = (msg) => toast.error(msg)
  const notifyB = (msg) => toast.success(msg)


 

  // posting image to cloudinary
  const postDetails = () => {

    console.log(body, image)
    const data = new FormData()
    data.append("file", image)
    data.append("upload_preset", "Social")
    data.append("cloud_name", "Pillai-ig")
    fetch("https://api.cloudinary.com/v1_1/Pillai-ig/image/upload", {
      method: "post",
      body: data
    }).then(res => res.json())
      .then(data => setUrl(data.url))
      .catch(err => console.log(err))
      console.log(data)
    console.log(url)
    // console.log(localStorage.getItem(storedData.token))

  }


  const loadfile = (event) => {
    const file = event.target.files[0];
    const url = URL.createObjectURL(file);
    setImageSrc(url);

    // Optionally, if you want to free memory when the component unmounts
    return () => URL.revokeObjectURL(url);
  };


  
  useEffect(() => {

    // saving post to mongodb
    if (url) {
    
      if (url) {
    
        // console.log(storedData.token);
        fetch("http://localhost:5000/Notice", {
          method: "post",
          headers: {
            "Content-Type": "application/json",
            "authorization": "Bearer " + storedData.token
          },
          body: JSON.stringify({
            body,
            pic: url
          })
        }).then(res => res.json())
          .then(data => {
            if (data.error) {
              notifyA(data.error)
            } else {
              notifyB("Successfully Posted")
              // navigate("/")
            }
          })
          .catch(err => console.log(err))
      }
  
    } 
  },[url])

  return (
    <>
      <Navbar />
      <div className="notices-container">
        <h2>College Notice Board</h2>
        <div id="noticeList"></div>
      </div>
      <hr />
      <div className="notice-form-container">
        {/* <form id="addNoticeForm" > */}
          <div className="form-group">
            <label htmlFor="noticeText">Add Notice:</label>
            <textarea value={body} onChange={(e) => {
          setBody(e.target.value) }}className="form-control" id="noticeText" rows="3" required></textarea>
          </div>
          <div className="form-group">
            <label htmlFor="uploadImage">Upload Image (Optional):</label>
            <img src={imageSrc || 'https://static.thenounproject.com/png/777906-200.png'} id="output" alt="Uploaded" />
            <input
              type="file"
              onChange={(event) => {
                loadfile(event);
                setImage(event.target.files[0])
              }}
              name="uploadImage"
              id="uploadImage"
              accept="image/*"
            />
          </div>
          <button type="submit" onClick={() => { postDetails() }} className="btn btn-primary">
            Post Notice
          </button>
        {/* </form> */}
      </div>
      <ToastContainer/>
              <AllNotice/>
    </>
  );
}
