import React,{useEffect,useState} from 'react'
import Logo from '../img/logo.jpeg';
import '../CSS/Student.css';
import {toast,ToastContainer} from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
export default function Student() {
  const [UserName, setUserName] = useState("");
  const [Email, setEmail] = useState("");
  const [Password, setPassword] = useState("");
  const notify = () => toast("Wow so easy!");
  useEffect(() => {
  document.getElementById('registrationForm').addEventListener('submit',
  function (event) {
      event.preventDefault(); // Prevent the default form submission
      const username = document.getElementById('username').value;
      const email = document.getElementById('email').value;
      const password = document.getElementById('password').value;
      const confirmPassword =
          document.getElementById('confirmPassword').value;
      const emailPattern =
          /^[a-zA-Z]{1,20}[0-9]{2}[a-zA-Z]{2,5}@student\.mes\.ac\.in$/;
      if (!emailPattern.test(email)) {
          alert('Registration failed. Invalid email format.');
          return;
      }
      if (password !== confirmPassword) {
          alert("Passwords don't match. Please re-enter.");
          return;
      }
      alert(`Registration successful!\nUsername: ${username}\nEmail:
${email}`);
      window.location.href = 'profile.html';
      // Here you can add code to send the form data to your server or perform other actions
  });

}, []);
  return (

   <>
   <body>
<div className="form-container">
<div className="user-icon">
{/* <!-- Update the image source with a valid URL or a relative path --> */}
<img src={Logo} alt="Pillai College Logo"/>
</div>
<form id="registrationForm">
<div className="form-group">
<label htmlFor="username"><i className="fas fa-user"></i> Username:</label>
<input type="text" id="username" name="username" value={UserName}  onChange={(e) => { setUserName(e.target.value) }} required/>
</div>
<div className="form-group">
<label htmlFor="email"><i className="fas fa-envelope"></i> Email:</label>
<input type="email" id="email" name="email" value={Email}  onChange={(e) => { setEmail(e.target.value) }} required/>
</div>
<div className="form-group">
<label htmlFor="password"><i className="fas fa-lock"></i> Password:</label>
<input type="password" id="password" name="password" value={Password}  onChange={(e) => { setPassword(e.target.value) }} required/>
</div>
<div className="form-group">
<label htmlFor="confirmPassword"><i className="fas fa-lock"></i> Confirm
Password:</label>
<input type="password" id="confirmPassword"
name="confirmPassword" required/>
</div>
<div className="form-group">
<input type="submit" onClick={notify} value="Register"/>
<ToastContainer/>
</div>
</form>
</div>
</body>
   </>
  )
}
